import React, { useState, useEffect } from 'react'
import { useLocation } from 'react-router'
import queryString from 'query-string'
import { Characters } from '../models/Characters';
import Card from '../components/Card';

const SearchScreen = ({ history }) => {
    const location = useLocation();
    //Recuperamos el parametro de busqueda con queryString y location
    const { q = "" } = queryString.parse(location.search);

    //Agregamos un estado
    const [inputValue, setInputValue] = useState(q);
    const [characters, setCharacters] = useState([]);

    const handleChange = (e) => {
        const value = e.target.value;
        setInputValue(value);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        //Usamos el metodo push para agregar un query param a la ruta
        history.push(`?q=${inputValue}`);
    }

    //Funcion para consultar el personaje con base al parametro
    const getChacacters = () => {
        if (inputValue.trim() !== ""){
            const value = inputValue.toLocaleLowerCase();
            const newValue = Characters.filter((character) =>
            character.name.toLocaleLowerCase().includes(value));
            setCharacters(newValue);
        }
        else {
            setCharacters([]);
        }
    }

    //Agregamos un useEffect para onvocar al getCharacters cada vez que cambia q
    useEffect(() => {
        //console.log(characters);
        getChacacters();
    }, [q]);


    return (
        <div className="container">
            <h1>Search Screen</h1>
            <hr />

            <div className="row">
                <div className="col-6">
                    <h2>Search</h2>
                    <form onSubmit={handleSubmit}>
                        <label className="form-label w-100">
                            Character: {""}
                            <input className="form-control" placeholder="Name Character" type="text" value={inputValue} onChange={handleChange} />
                        </label>
                        <button type="submit" className="btn btn-info w-100">Search</button>
                    </form>
                </div>
                <div className="col-6">
                    <h2>Results: {characters.length}</h2>
                    {
                        characters.length === 0 && (
                            <div className="alert alert-warning text-center">
                                Please Search a Character
                            </div>
                        )
                    }
                    {
                        characters.map((character) => (
                            <Card key={character.id} {...character} />
                        ))
                    }
                </div>
            </div>
        </div>
    )
}

export default SearchScreen
