import React, { useReducer, useEffect } from 'react'
import { AuthContext } from './context/AuthContext'
import { AuthReducer } from './reducers/AuthReducer'
import AppRouter from './routes/AppRouter'

//Definimos la funcion init, para intentar recuperar el estado log desde el localstorage.
const init = () => {
    return JSON.parse(localStorage.getItem('log')) || {log: false}
}

const App = () => {

    //definimos el context para toda la aplicación mediante el hook useContext
    const [log, dispatch] = useReducer(AuthReducer, {}, init);

    //Definimos un useEffect para persistir el estado del log
    useEffect(() => {
        localStorage.setItem('log', JSON.stringify(log));
    }, [log])

    //Usamos <AuthContext.Provider> para proveer el contexto a toda la aplicacion
    return (
        <AppRouter/>
    )
}

export default App
