import React from 'react';
import { useParams } from 'react-router';
import { Characters } from '../models/Characters';

const CharacterScreen = ({ history }) => {
    //Usamos el hook useParams de 'react-router' para tener acceso a los parámetros de la ruta.
    const { id } = useParams();

    //Filtramos los Personajes en base al id y desestructuramos los datos.
    const { type, name, description } = Characters.find((character) => character.id === id);

    const path = `/assets/${type}-${id}.png`;

    const handleBack = () => {
        history.goBack(); //Regresamos hacia atras en el historial
    };

    return (
        <div className="container row mt-3">
            <div className="col-8">
                <img className="img-thumbnail"
                style={{
                    width: "auto", height: "400px"
                }}
                src={path}
                alt={id} />
            </div>
            <div className="col-4">
                <h2>Nombre: {name}</h2>´
                <p>Descripción: {description}</p>
                <button onClick={handleBack} className="btn btn-outline-warning">Go Back</button>
            </div>
        </div>
    );
};

export default CharacterScreen;
