import React, { useEffect, useState } from 'react'
import { saveAs } from 'file-saver'

const AddCharacterScreen = () => {
    const [idValue, setIdValue] = useState('');
    const [nameValue, setNameValue] = useState('');
    const [descValue, setDescValue] = useState('');
    const [typeValue, setTypeValue] = useState('');

    const [txtCharacters, setTxtCharacters] = useState('');

    const updateFile = (e) => {
        const file = e.target.files[0];
        if ( !file ) return;

        const fileReader = new FileReader();
        fileReader.readAsText(file);

        fileReader.onload = () => {
            console.log(fileReader.result);
            setTxtCharacters(fileReader.result);
        }
        fileReader.onerror = () => {
            console.log(fileReader.error);
        }
    }

    const handleChange = (e) => {
        switch (e.target.id) {
            case 'charId': setIdValue(e.target.value); break;
            case 'charName': setNameValue(e.target.value); break;
            case 'charDesc': setDescValue(e.target.value); break;
            case 'charType': setTypeValue(e.target.value); break;
            default: break;
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        
        setTxtCharacters(txtCharacters.slice(0, -2) + '{ id: "' + idValue + '", name: "' + nameValue + '", description: "' + descValue + '", type: "' + typeValue + '", }, ];');
    }
    
    useEffect(() => {
        const blob = new Blob([txtCharacters], { type: 'plain/text;charset=utf-8' });
        saveAs( blob, 'Characters.js');
    }, [txtCharacters])

    return (
        <div className="container">
            <h1>Add Character Screen</h1>
            <hr />

            <div className="row">
                <div className="col-6">
                    <h2>Add</h2>
                    <form onSubmit={handleSubmit}>
                    <label className="form-label w-100">
                            File: {""}
                            <input className="form-control" placeholder="File" type="file" onChange={updateFile} />
                        </label>
                        <label className="form-label w-100">
                            id: {""}
                            <input id="charId" className="form-control" placeholder="Id Character" type="text" value={idValue} onChange={handleChange} />
                        </label>
                        <label className="form-label w-100">
                            name: {""}
                            <input id="charName" className="form-control" placeholder="Name Character" type="text" value={nameValue} onChange={handleChange} />
                        </label>
                        <label className="form-label w-100">
                            description: {""}
                            <input id="charDesc" className="form-control" placeholder="Description Character" type="text" value={descValue} onChange={handleChange} />
                        </label>
                        <label className="form-label w-100">
                            type: {""}
                            <input id="charType" className="form-control" placeholder="Type Character" type="text" value={typeValue} onChange={handleChange} />
                        </label>
                        <button type="submit" className="btn btn-info w-100">Search</button>
                    </form>
                    <p>{txtCharacters}</p>
                </div>
            </div>
        </div>
    )
}

export default AddCharacterScreen
