import React, { useContext } from 'react'
import { NavLink, useHistory } from 'react-router-dom'
import { AuthContext } from '../context/AuthContext';
import { authTypes } from '../types/authTypes';

const Navbar = () => {

    let estadoMenu = false;

    const MostrarOcultarMenu = () => {
        padre = document.getElementById('nav');
        enlaces = padre.getElementsByClassName('link');
        for (let i = 0; i < enlaces.length; i++) {
            if (estadoMenu) enlaces[i].classList.remove("mostrar-menu");
            else enlaces[i].classList.add("mostrar-menu");
        }
        estadoMenu = !estadoMenu;
    }

    const CambiarActivo = (a) => {
        if (!a.classList.contains('activo')){
            elementos = document.getElementsByClassName('activo');
            for (let i = 0; i < elementos.length; i++) {
                elementos[i].classList.remove('activo');
            }
            a.classList.add('activo');
        }
    }

    return(
        <nav className="nav">
            <NavLink className="activo link" aria-current="page" to="/">Inicio</NavLink>
            <NavLink className="link" aria-current="page" to="/participantes">Participantes</NavLink>
            <div className="nav-menu-icon" onClick={MostrarOcultarMenu()}>
                <p>â˜°</p>
            </div>
        </nav>
    )
}

export default Navbar
