import React, { useContext } from 'react'
import { NavLink, useHistory } from 'react-router-dom'
import { AuthContext } from '../context/AuthContext';
import { authTypes } from '../types/authTypes';

const Navbar = () => {
    //Recuperamos el context y el dispatch para poderlo modificar
    const { dispatch } = useContext(AuthContext);

    const history = useHistory();

    const handleLogout = () => {
        //console.log(history);

        dispatch({type: authTypes.logout});
        
        //Usando el método replace del history para reemplazar una ruta por otra
        history.replace("/login");
    }

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-info">
            <div className="container-fluid">
                <h2 className="navbar-brand">DBZ App</h2>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon" />
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <NavLink activeClassName="active text-white" className="nav-link" aria-current="page" to="/men">Men</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink activeClassName="active text-white" className="nav-link" aria-current="page" to="/women">Women</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink activeClassName="active text-white" className="nav-link" aria-current="page" to="/search">Search</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink activeClassName="active text-white" className="nav-link" aria-current="page" to="/addCharacter">Add Character</NavLink>
                        </li>
                    </ul>
                    <div className="d-flex">
                        <button className="btn btn-danger" onClick={handleLogout}>Logout</button>
                    </div>
                </div>
            </div>
        </nav>
    )
}

export default Navbar
