import React, { useContext } from 'react'
import { useHistory } from 'react-router';
import { AuthContext } from '../context/AuthContext';
import { authTypes } from '../types/authTypes';

const LoginScreen = () => {
    //Recuperamos el context y el dispatch para poderlo modificar
    const { dispatch } = useContext(AuthContext);

    //Recuperamos el history mediante useHistory
    const history = useHistory();

    const handleLogin = () => {
        //Usamos el dispatch para modificar el context y establecer log-true
        dispatch({type: authTypes.login});

        //El metodo push mete una nueva ruta en la pila
        history.push("/");
    }

    return (
        <div className="container mt-5 text-center">
            <img src="assets/animate.gif" alt="" />
            <h1 className="my-3">Login Screen</h1>
            <button className="btn btn-primary" onClick={handleLogin}>Login</button>
        </div>
    )
}

export default LoginScreen
